import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:firebase_core/firebase_core.dart';
import 'firebase_options.dart'; // Ensure you have the Firebase options imported
import 'login_page.dart'; // Import your login page
import 'shoe_store_page.dart'; // Import shoe store page
import 'cart_page.dart'; // Import cart page
import 'shoe_item.dart'; // Import shoe item model

void main() async {
  WidgetsFlutterBinding.ensureInitialized();
  await Firebase.initializeApp(options: DefaultFirebaseOptions.currentPlatform);
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return CupertinoApp(
      debugShowCheckedModeBanner: false,
      theme: const CupertinoThemeData(
        brightness: Brightness.light,
      ),
      initialRoute: '/', // Set the initial route to login page
      routes: {
        '/': (context) => LoginPage(), // Route to LoginPage
        '/shoeApp': (context) => ShoeApp(), // Route to ShoeApp
      },
    );
  }
}

class ShoeApp extends StatefulWidget {
  const ShoeApp({super.key});

  @override
  _ShoeAppState createState() => _ShoeAppState();
}

class _ShoeAppState extends State<ShoeApp> {
  final List<ShoeItem> shoes = [
    ShoeItem(name: 'Nike Air Max', price: 100.0, selectedSize: 7, imageUrl: 'data:image/jpeg;base64,'),
    ShoeItem(name: 'Adidas Superstar', price: 120.0, selectedSize: 7, imageUrl: 'data:image/webp;base64,'),
    ShoeItem(name: 'Puma RS-X', price: 90.0, selectedSize: 7, imageUrl: 'data:image/jpeg;base64,'),
    ShoeItem(name: 'Skechers', price: 80.0, selectedSize: 7, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSAt81mbrun_5o4CfcfIgqd01H25kkqu3_euQ&s'),
    ShoeItem(name: 'New Balance', price: 110.0, selectedSize: 7, imageUrl: 'data:image/webp;base64,'),
    ShoeItem(name: 'Converse', price: 85.0, selectedSize: 7, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTAHtCmA_RepVfTf_9blQPO98NK3yYWg49VaA&s'),
    ShoeItem(name: 'Air Jordans', price: 150.0, selectedSize: 7, imageUrl: 'data:image/webp;base64,'),
    ShoeItem(name: 'Gucci Shoes', price: 300.0, selectedSize: 7, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQeWoEbkTpCdSky7NdwHq2vE2XK8_3ZLuduLA&s'),
    ShoeItem(name: 'Adidas Football Boots', price: 130.0, selectedSize: 7, imageUrl: 'data:image/webp;base64,'),
  ];

  List<ShoeItem> cartItems = [];

  @override
  Widget build(BuildContext context) {
    return CupertinoApp(
      debugShowCheckedModeBanner: false,
      theme: const CupertinoThemeData(
        brightness: Brightness.light,
      ),
      home: CupertinoTabScaffold(
        tabBar: CupertinoTabBar(
          items: const [
            BottomNavigationBarItem(
              icon: Icon(CupertinoIcons.home),
              label: 'Store',
            ),
            BottomNavigationBarItem(
              icon: Icon(CupertinoIcons.shopping_cart),
              label: 'Cart',
            ),
          ],
        ),
        tabBuilder: (context, index) {
          if (index == 0) {
            // Shoe Store Page
            return ShoeStorePage(
              shoes: shoes,
              onAddToCart: (ShoeItem shoe) {
                setState(() {
                  cartItems.add(shoe);
                });
              },
            );
          } else {
            // Cart Page
            return CartPage(
              cartItems: cartItems,
              onRemoveFromCart: (ShoeItem shoe) {
                setState(() {
                  cartItems.remove(shoe); // Remove the item from the cart
                });
              },
            );
          }
        },
      ),
    );
  }
}
